ox-body\">	 \n					<table id=\"searchTable\">\n						<tr>           \n				#foreach($column in $columns)    \n					#if(!${column.isIdentityPk})      \n					<th w_index=\"${column.javaFieldName}\">${column.javaFieldName}</th>\n					#end    \n				#end \n				<th w_render=\"operate\" width=\"10%;\">操作</th>\n						</tr>\n					</table>\n				</div><!-- /.box-body -->\n			</div>\n		    \n		    <div id=\"crudWin\">\n			    	<form id=\"crudFrm\" class=\"form-horizontal\">\n						#foreach($column in $columns)\n					   #if(!${column.isIdentityPk})						\n						<div class=\"form-group\">\n	                      <label class=\"col-sm-3 control-label\">${column.javaFieldName}</label>\n	                      <div class=\"col-sm-7\">\n	                        <input name=\"${column.javaFieldName}\" type=\"text\" class=\"form-control\" required=\"true\">\n	                      </div>\n	                    </div>\n					   #end\n					#end			    		\n					</form>\n			    </div>\n		    \n<script type=\"text/javascript\">     \nvar that = this;\n\nvar pk = \'${context.javaPkName}\'; // java类中的主键字段\nvar listUrl = ctx + \'listBsgrid${context.javaBeanName}.do\'; // 查询\nvar addUrl = ctx + \'addBsgrid${context.javaBeanName}.do\'; // 添加\nvar updateUrl = ctx + \'updateBsgrid${context.javaBeanName}.do\'; // 修改\nvar delUrl = ctx + \'delBsgrid${context.javaBeanName}.do\'; // 删除\nvar submitUrl = \'\'; // 提交URL\n\nvar gridObj; // 表格\nvar crudWin; // 窗口\nvar ${jq}schFrm = ${jq}(\'#schFrm\'); // 查询表单\nvar ${jq}crudFrm = ${jq}(\'#crudFrm\'); // 编辑表单\n\nvar ${jq}schBtn = ${jq}(\'#schBtn\'); // 查询按钮\nvar ${jq}addBtn = ${jq}(\'#addBtn\'); // 添加按钮\n\nvar validator; // 验证器\n\nfunction reset() {\n	${jq}crudFrm.get(0).reset();\n	validator.resetForm();\n}\n\n\n// 初始化事件\n${jq}addBtn.click(function() {\n	submitUrl = addUrl;\n	reset();\n	crudWin.title(\'添加\');\n	crudWin.show();	\n});\n\n${jq}schBtn.click(function() {\n	search();\n});\n\ngridObj = ${jq}.fn.bsgrid.init(\'searchTable\', {\n	url: listUrl\n    ,pageSizeSelect: true\n    ,stripeRows: true\n    ,rowHoverColor: true\n    ,rowSelectedColor: false\n    ,pageSize: 10\n});\n\ncrudWin = dialog({\n	title: \'编辑\',\n	width:400,\n	content: document.getElementById(\'crudWin\'),\n	okValue: \'保存\',\n	ok: function () {\n		that.save();\n		return false;\n	},\n	cancelValue: \'取消\',\n	cancel: function () {\n		this.close();\n		return false;\n	}\n});\n\nfunction search(){\n	gridObj.options.otherParames = getFormData(${jq}schFrm);\n	gridObj.gotoPage(1);\n}\n\nfunction operate(row, rowIndex, colIndex, options) {\n	return \'<a href=\"#\" onclick=\"\'\n		+ FunUtil.createFun(that, \'edit\', row)\n		+ \' return false;\">修改</a>\'\n		+ \'&nbsp;&nbsp;\'\n		+ \'<a href=\"#\" onclick=\"\'\n		+ FunUtil.createFun(that, \'del\', row)\n		+ \' return false;\">删除</a>\';\n}\n\n// 保存\nthis.save = function() {\n	var self = this;\n	var data = getFormData(${jq}crudFrm);\n	var validateVal = validator.form();\n	if(validateVal) {\n		Action.post(submitUrl, data, function(result) {\n			Action.execResult(result, function(result) {\n				gridObj.refreshPage();\n				crudWin.close();\n			});\n		});\n	}\n}\n // 编辑\nthis.edit = function(row) {\n	if (row) {\n		submitUrl = updateUrl + \'?\' + pk + \'=\' + row[pk];\n		reset();\n		crudWin.title(\'修改\');\n		loadFormData(${jq}crudFrm,row);		\n		crudWin.show();\n	}\n}\n\n// 删除\nthis.del = function(row) {\n	if (row) {\n		var d = dialog({\n			title: \'提示\',\n			width: 200,\n			content: \'确定要删除该记录吗?\',\n			okValue: \'确定\',\n			ok: function () {\n				Action.post(delUrl, row, function(result) {\n					Action.execResult(result, function(result) {\n						gridObj.refreshPage();\n					});\n				});\n			},\n			cancelValue: \'取消\',\n			cancel: function () {}\n		});\n		d.showModal();\n	}\n}\n\nvalidator = ${jq}crudFrm.validate();\n</script>\n\n        </section><!-- /.content -->\n      </div><!-- /.content-wrapper -->\n\n      <!-- Main Footer -->\n      <footer class=\"main-footer\">\n        <!-- Default to the left -->\n        <strong>Copyright &copy; 2016 durcframework.</strong> All rights reserved.\n      </footer>\n\n    </div><!-- ./wrapper -->\n\n    <!-- REQUIRED JS SCRIPTS -->\n    <!-- Bootstrap 3.3.5 -->\n    <script src=\"${jq}{AdminLTE}bootstrap/js/bootstrap.min.js\"></script>\n    <!-- AdminLTE App -->\n    <script src=\"${jq}{AdminLTE}dist/js/app.min.js\"></script>\n\n    <!-- Optionally, you can add Slimscroll and FastClick plugins.\n         Both of these plugins are recommended to enhance the\n         user experience. Slimscroll is required when using the\n         fixed layout. -->\n  </body>\n</html>\n','admin');

