  #if(!${column.isIdentityPk})               \n        #if($i > 0),#end #{${column.javaFieldName},jdbcType=${column.mybatisJdbcType}}\n               #set($i=$i +1)\n            #end          \n        #end\n \n        )\n    </insert>\n    <select id=\"queryCnt\" parameterType=\"com.shunwang.business.framework.mybatis.query.ConditionQuery\" resultType=\"java.lang.Integer\" >\n        select count(*) from ${table.tableName} t\n        <include refid=\"conditionQuery.core\" />\n    </select>\n\n    <update id=\"update\" parameterType=\"${context.packageName}.${context.javaBeanNameLF}.pojo.${context.javaBeanName}\">\n    UPDATE ${table.tableName}\n    SET \n#set ($i=0) \n        #foreach($column in $columns) \n            #if(!${column.isPk})               \n        #if($i > 0),#end ${column.columnName}=#{${column.javaFieldName},jdbcType=${column.mybatisJdbcType}}\n               #set($i=$i +1)\n            #end          \n        #end    \n    WHERE ${context.pkName} = #{${context.javaPkName},jdbcType=${context.mybatisPkType}}\n    </update>\n</mapper>','admin'),(23,'durc-jspFduiCRUD','jsp',NULL,'${context.javaBeanNameLF}.jsp','<%@ page language=\"java\" contentType=\"text/html; charset=UTF-8\"\n    pageEncoding=\"UTF-8\"%>\n<%@ include file=\"../taglib.jsp\" %>\n<!DOCTYPE html PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" \"http://www.w3.org/TR/html4/loose.dtd\">\n<html>\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<title>增删改查</title>\n</head>\n<body>\n<div class=\"search-wrap\">\n		<div class=\"search-content\">\n        	<table class=\"search-tab\">\n				<tr>\n#foreach($column in $columns)\n    #if(!${column.isIdentityPk})\n            <th>${column.javaFieldName}:</th><td id=\"txt-${column.javaFieldName}Sch\"></td>      \n    #end\n#end\n					<td>\n						<div id=\"btnSch\"></div>\n					</td>\n				</tr>\n       		</table>\n       </div>\n   </div>\n   <div class=\"result-wrap\">\n		<div class=\"result-title\">\n             <div class=\"result-list\">\n                 <a id=\"addNew\" href=\"javascript:void(0)\"><i class=\"icon-font\"></i>新增</a>\n             </div>\n		</div>\n		<div class=\"result-content\">\n			<div id=\"grid\"></div>\n		</div>\n   </div>\n   \n   <div id=\"crudWin\" style=\"display: none;\">\n   		<table class=\"insert-tab\" width=\"100%\">\n   		<caption id=\"formMsg\"></caption>\n        	<tbody>\n#foreach($column in $columns)\n    #if(!${column.isIdentityPk})\n        <tr>\n            <th>${column.javaFieldName}:</th><td id=\"txt-${column.javaFieldName}\"></td>\n        </tr>\n    #end\n#end\n         	</tbody>\n         </table>\n   </div>\n\n<script type=\"text/javascript\">\n(function(){\n	\nvar schPanel;\nvar grid;\nvar formPanel;\nvar crudWin;\n\nvar pk = \'${context.javaPkName}\'; // java类中的主键字段\nvar listUrl = ctx + \'list${context.javaBeanName}.do\'; // 查询\nvar addUrl = ctx + \'add${context.javaBeanName}.do\'; // 添加\nvar updateUrl = ctx + \'update${context.javaBeanName}.do\'; // 修改\nvar delUrl = ctx + \'del${context.javaBeanName}.do\'; // 删除\n	\nschPanel = new FDFormPanel({\n	controls:[\n#set($i=0)\n#foreach($column in $columns) \n    #if(!${column.isIdentityPk})\n       #if($i>0),#end   new FDTextBox({domId:\'txt-${column.javaFieldName}Sch\',name:\'${column.javaFieldName}Sch\'})      \n      #set($i=$i+1)   \n #end\n#end\n	]\n});\n\nnew FDButton({domId:\'btnSch\',text:\'查询\',onclick:function(){\n	search();\n}});\n\ngrid = new FDGrid({\n	domId:\'grid\'\n	,url:listUrl\n	,columns:[\n#set($i=0)\n#foreach($column in $columns)\n    #if(!${column.isIdentityPk})    \n    #if($i>0),#end {text:\'${column.javaFieldName}\',name:\'${column.javaFieldName}\'}\n    #set($i=$i+1)\n    #end    \n#end \n	]\n	,actionButtons:[\n		{text:\'修改\',onclick:update}\n		,{text:\'删除\',onclick:del}\n	]\n});\n\ncrudWin = new FDWindow({\n	contentId:\'crudWin\'\n	,buttons:[\n		{text:\'保存\',onclick:function(){\n			formPanel.save();\n		}}\n		,{text:\'取消\',onclick:function(){\n			crudWin.hide();\n		}}\n	]\n});\n\nformPanel = new FDFormPanel({\n	grid:grid\n	,win:crudWin\n	// 服务器端的请求\n	,crudUrl:{\n		add: addUrl\n		,update: updateUrl\n		,del: delUrl\n	}\n	,controls:[\n	    new FDHidden({name:pk,defaultValue:0})\n#foreach($column in $columns)\n    #if(!${column.isIdentityPk})\n        ,new FDTextBox({domId:\'txt-${column.javaFieldName}\',name:\'${column.javaFieldName}\',msgId:\'formMsg\',width:200\n			,validates:[\n		     {rule:{notNull:true},successClass:\'green\',errorClass:\'require-red\',errorMsg:\'${column.javaFieldName}不能为空\'}\n             ]\n        })\n    #end\n#end\n	]\n});\n\n$(\'#addNew\').click(function(){\n	add();\n});\n\nfunction add() {\n	formPanel.add();\n}\n\nfunction update(rowData,rowIndex) {\n	formPanel.update(rowData);\n}\n\n\nfunction del(row,rowIndex) {\n	formPanel.del(row);\n}\n\nfunction search() {\n	grid.search(schPanel.getData());\n}\n	\n})();\n</script>\n</body>\n</html>','admin');

